<script>
    let slider = document.querySelector(".slider");
    let sliderFirst = document.querySelector(".global-slider-header-before");
    let sliderEnd = document.querySelector(".global-slider-header-after");
    let desc = document.querySelector('.global-detail-desc');

    sliderFirst.addEventListener('click', () => {
        desc.textContent =
            `To lead the beauty industry with pioneering IoT-based technologies, enhancing global skincare standards and client satisfaction.`;
        slider.classList.remove('isActive');
    });
    sliderEnd.addEventListener('click', () => {
        desc.textContent = `Integrating technology into the beauty sector. From pioneering IoT
                advancements to redefining skincare practices, we're dedicated to
                enhancing beauty treatments through innovation. Discover how we're
                shaping the future of beauty with advanced technologies that elevate
                client experiences and set new industry standards.`;
        slider.classList.add('isActive');
    });
</script>
