    <script>
        function changeMode(el) {
            let pel = document.querySelector(".circular-parent");
            let arrEl = document.querySelectorAll(".circular-child");

            arrEl.forEach(element => {
                element.classList.remove('isActive');
            });

            let rotation = "0deg";
            let outlineColor = "#F0BAA0";
            let imageSrc = '';

            if (el.classList.contains("circle-first")) {
                rotation = "-2deg";
                outlineColor = "#F0BAA0";
                imageSrc = '{{asset('assets/file/thing-orange-2x.png')}}';
            } else if (el.classList.contains('circle-second')) {
                rotation = "-39deg";
                outlineColor = "#D89271";
                imageSrc = '{{asset('assets/file/thing-oranges.png')}}';
            } else if (el.classList.contains('circle-third')) {
                rotation = "151deg";
                outlineColor = "#F0BAA0";
                imageSrc = '{{asset('assets/file/thing-pink-2x.png')}}';
            } else if (el.classList.contains('circle-fourth')) {
                rotation = "198deg";
                outlineColor = "#F0BAA0";
                imageSrc = '{{asset('assets/file/thing-purple-2x.png')}}';
            }

            pel.style.rotate = rotation;
            el.style.outline = `1.5px solid ${outlineColor}`;
            el.classList.add('isActive');

            $(".thing-real").addClass('isRotating');
            setTimeout(() => {
                $(".thing-real").addClass('isRotated')
                                .removeClass('isRotating');
                $(".reals, .shadow").prop('src', imageSrc);
                setTimeout(() => {
                    $(".thing-real").removeClass('isRotated');
                }, 700);
            }, 400);
        }

        $(document).ready(function() {
            var clientIP = "{{ \Illuminate\Support\Facades\Request::getClientIp(true) }}";

            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(position => {
                    var latitude = position.coords.latitude;
                    var longitude = position.coords.longitude;
                    var geocodeUrl = `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`;

                    $.getJSON(geocodeUrl, data => {
                        if (data && data.address) {
                            var city = data.address.county || data.address.city;
                            var country = data.address.country;

                            if (city && country) {
                                var device = '';
                                const userAgent = navigator.userAgent;

                                if (userAgent.includes("Chrome")) {
                                    device = "Chrome";
                                } else if (userAgent.includes("Firefox")) {
                                    device = "Firefox";
                                } else if (userAgent.includes("Safari")) {
                                    device = "Safari";
                                } else if (userAgent.includes("Edge")) {
                                    device = "Edge";
                                } else if (userAgent.includes("Trident")) {
                                    device = "Trident";
                                } else {
                                    device = "Unknown";
                                }

                                var headers = {
                                    'url': 'module/visitor/store'
                                };
                                var params = {
                                    page             : 'Home',
                                    location_country : country,
                                    location_city    : city,
                                    device           : device,
                                    ip_addr          : clientIP,
                                };

                                $.ajax({
                                    url: "{{ route('api.post') }}",
                                    type: 'POST',
                                    data: params,
                                    headers: headers,
                                    beforeSend: function() {
                                    },
                                    success: function(response) {
                                        console.log(response);
                                    },
                                    error: function(xhr, ajaxOptions, thrownError) {
                                        console.log('error');
                                    }
                                });
                            } else {
                                console.log("City and country not found in the location data.");
                            }
                        }
                    }).fail(() => {
                        console.log("Error in retrieving geocoding data.");
                    });
                }, error => {
                    if (error.code === error.PERMISSION_DENIED) {
                        console.log("Location access denied by the user.");
                    } else {
                        console.log("Error in retrieving location.");
                    }
                });
            } else {
                console.log("Geolocation is not supported by this browser.");
            }
        });
    </script>

